using Microsoft.AspNetCore.Mvc;namespace AWAQ{    public class Usuario    {        public int id_admin { get; set; }        public int id_usuario { get; set; }        public string correo { get; set; }        public string contrasena { get; set; }        public string pais { get; set; }        public string? ultima_conexion { get; set; }        public string username { get; set; }        public int edad { get; set; }        public int tiempo_jugado { get; set; }        public int metodos_completados { get; set; }        public string Nombre { get; set; }        [BindProperty]        public int progreso { get; set; }        [BindProperty]        public string estatus { get; set; }        [BindProperty]        public string css { get; set; }        [BindProperty]        public string acceso { get; set; }        public Usuario()        {        }    }}